// Hero.tsx
// Provide { headline, subheadline, primaryCTA, secondaryCTA, clientLogos } from /api/homepage

import { useState, useEffect } from 'react';

interface HeroProps {
  headline: string;
  subheadline: string;
  primaryCTA: { text: string; href: string };
  secondaryCTA: { text: string; href: string };
  videoSrc: string;
  clientLogos?: Array<{ name: string; src: string; href: string }>;
}

export function Hero({
  headline,
  subheadline,
  primaryCTA,
  secondaryCTA,
  videoSrc,
  clientLogos = []
}: HeroProps) {
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setIsLoaded(true), 100);
    return () => clearTimeout(timer);
  }, []);

  return (
    <section className="relative min-h-screen flex items-center justify-center overflow-hidden" aria-labelledby="hero-headline">
      {/* Video Background */}
      <div className="absolute inset-0 z-0" aria-hidden="true">
        <video
          autoPlay
          loop
          muted
          playsInline
          className="absolute inset-0 w-full h-full object-cover"
          style={{ filter: 'brightness(0.4)' }}
        >
          <source src={videoSrc} type="video/mp4" />
        </video>
        <div className="absolute inset-0 bg-gradient-to-b from-black/60 via-black/40 to-black/60" />
      </div>

      {/* Hero Content */}
      <div className="relative z-10 container mx-auto px-4 py-32 max-w-5xl text-center">
        <h1
          id="hero-headline"
          className="font-heading font-bold text-white text-5xl md:text-6xl lg:text-7xl mb-6 leading-tight"
          style={{
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? 'translateY(0)' : 'translateY(30px)',
            transition: 'all 250ms cubic-bezier(0.4, 0, 0.2, 1)'
          }}
        >
          {headline}
        </h1>

        <p
          className="font-body text-xl md:text-2xl text-white/90 mb-12 max-w-3xl mx-auto"
          style={{
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? 'translateY(0)' : 'translateY(20px)',
            transition: 'all 250ms cubic-bezier(0.4, 0, 0.2, 1) 150ms'
          }}
        >
          {subheadline}
        </p>

        <div
          className="flex flex-col sm:flex-row gap-4 justify-center items-center mb-16"
          style={{
            opacity: isLoaded ? 1 : 0,
            transform: isLoaded ? 'translateY(0)' : 'translateY(20px)',
            transition: 'all 250ms cubic-bezier(0.4, 0, 0.2, 1) 300ms'
          }}
        >
          <a
            href={primaryCTA.href}
            className="px-10 py-5 bg-primary-500 text-white font-semibold text-lg rounded-lg shadow-xl hover:shadow-2xl hover:bg-primary-600 active:bg-primary-700 transition-all duration-250 transform hover:scale-105 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-white focus-visible:ring-offset-2 focus-visible:ring-offset-primary-500 min-h-[44px]"
            aria-label={primaryCTA.text}
          >
            {primaryCTA.text}
          </a>

          <a
            href={secondaryCTA.href}
            className="px-10 py-5 bg-white/10 backdrop-blur-sm text-white border-2 border-white/50 font-semibold text-lg rounded-lg hover:bg-white/20 hover:border-white transition-all duration-250 transform hover:scale-105 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-white focus-visible:ring-offset-2 min-h-[44px]"
            aria-label={secondaryCTA.text}
          >
            {secondaryCTA.text}
          </a>
        </div>

        {/* Client Logos */}
        {clientLogos.length > 0 && (
          <div
            className="border-t border-white/20 pt-12"
            style={{
              opacity: isLoaded ? 1 : 0,
              transition: 'opacity 250ms cubic-bezier(0.4, 0, 0.2, 1) 450ms'
            }}
          >
            <p className="text-white/70 text-sm uppercase tracking-wide mb-8 font-heading font-medium">
              Trusted by industry leaders
            </p>
            <div className="flex flex-wrap justify-center items-center gap-8 md:gap-12">
              {clientLogos.map((logo) => (
                <a
                  key={logo.name}
                  href={logo.href}
                  className="opacity-60 hover:opacity-100 transition-opacity duration-250 grayscale hover:grayscale-0"
                  aria-label={`Visit ${logo.name} website`}
                >
                  <img
                    src={logo.src}
                    alt={logo.name}
                    className="h-8 md:h-10 w-auto"
                    loading="lazy"
                  />
                </a>
              ))}
            </div>
          </div>
        )}
      </div>
    </section>
  );
}

