// AnimatedStatCard.tsx
// Provide { value, suffix, label, description } from /api/stats

import { useEffect, useState, useRef } from 'react';

interface AnimatedStatCardProps {
  value: number;
  suffix?: string;
  label: string;
  description?: string;
  duration?: number;
  decimals?: number;
  delay?: number;
}

export function AnimatedStatCard({
  value,
  suffix = '',
  label,
  description,
  duration = 2000,
  decimals = 0,
  delay = 0
}: AnimatedStatCardProps) {
  const [count, setCount] = useState(0);
  const [isVisible, setIsVisible] = useState(false);
  const ref = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
          observer.disconnect();
        }
      },
      { threshold: 0.1 }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    if (!isVisible) return;

    const prefersReducedMotion = window.matchMedia('(prefers-reduced-motion: reduce)').matches;

    if (prefersReducedMotion) {
      setCount(value);
      return;
    }

    const startTime = Date.now() + delay;
    const startValue = 0;
    const endValue = value;

    const animate = () => {
      const now = Date.now();
      const elapsed = now - startTime;

      if (elapsed < 0) {
        requestAnimationFrame(animate);
        return;
      }

      if (elapsed < duration) {
        const progress = elapsed / duration;
        const easeOutQuart = 1 - Math.pow(1 - progress, 4);
        const currentValue = startValue + (endValue - startValue) * easeOutQuart;
        setCount(currentValue);
        requestAnimationFrame(animate);
      } else {
        setCount(endValue);
      }
    };

    requestAnimationFrame(animate);
  }, [isVisible, value, duration, delay]);

  return (
    <div
      ref={ref}
      className="group relative bg-white rounded-xl p-8 border border-neutral-200 hover:border-primary-500 hover:shadow-lg transition-all duration-250"
      role="article"
      aria-label={`${label}: ${value}${suffix}`}
    >
      {/* Decorative gradient on hover */}
      <div className="absolute inset-0 bg-gradient-to-br from-primary-50/0 to-primary-50/0 group-hover:from-primary-50/50 group-hover:to-primary-50/20 rounded-xl transition-all duration-250" />

      <div className="relative z-10">
        <div className="font-heading font-bold text-5xl md:text-6xl text-primary-500 mb-3">
          <span aria-hidden="true">
            {count.toFixed(decimals)}
          </span>
          {suffix && <span className="text-4xl">{suffix}</span>}
        </div>

        <h3 className="font-heading font-semibold text-lg text-neutral-800 mb-2">
          {label}
        </h3>

        {description && (
          <p className="font-body text-sm text-neutral-600 leading-relaxed">
            {description}
          </p>
        )}
      </div>

      {/* Screen reader announces final value */}
      <span className="sr-only">
        {value}{suffix} {label}. {description}
      </span>
    </div>
  );
}

codeSnippets.SkeletonList:
// SkeletonList.tsx
// Use while loading data from API (e.g., /api/blog, /api/case-studies)

interface SkeletonListProps {
  count?: number;
  variant?: 'card' | 'list' | 'grid';
}

export function SkeletonList({ count = 3, variant = 'card' }: SkeletonListProps) {
  const baseClasses = 'animate-pulse bg-neutral-200 rounded';

  const renderCardSkeleton = () => (
    <div className="bg-white rounded-xl border border-neutral-200 overflow-hidden" role="status" aria-label="Loading content">
      <div className={`${baseClasses} h-48 w-full`} />
      <div className="p-6 space-y-4">
        <div className={`${baseClasses} h-4 w-24`} />
        <div className={`${baseClasses} h-6 w-3/4`} />
        <div className="space-y-2">
          <div className={`${baseClasses} h-4 w-full`} />
          <div className={`${baseClasses} h-4 w-5/6`} />
        </div>
        <div className={`${baseClasses} h-10 w-32 mt-4`} />
      </div>
    </div>
  );

  const renderListSkeleton = () => (
    <div className="bg-white rounded-lg border border-neutral-200 p-6 space-y-4" role="status" aria-label="Loading content">
      <div className="flex items-center space-x-4">
        <div className={`${baseClasses} h-12 w-12 rounded-full`} />
        <div className="flex-1 space-y-2">
          <div className={`${baseClasses} h-4 w-3/4`} />
          <div className={`${baseClasses} h-3 w-1/2`} />
        </div>
      </div>
    </div>
  );

  const renderGridSkeleton = () => (
    <div className="bg-white rounded-lg border border-neutral-200 p-6" role="status" aria-label="Loading content">
      <div className={`${baseClasses} h-40 w-full mb-4 rounded-lg`} />
      <div className={`${baseClasses} h-5 w-3/4 mb-3`} />
      <div className="space-y-2">
        <div className={`${baseClasses} h-3 w-full`} />
        <div className={`${baseClasses} h-3 w-5/6`} />
      </div>
    </div>
  );

  const renderSkeleton = () => {
    switch (variant) {
      case 'list':
        return renderListSkeleton();
      case 'grid':
        return renderGridSkeleton();
      default:
        return renderCardSkeleton();
    }
  };

  return (
    <div className="space-y-6">
      {Array.from({ length: count }).map((_, i) => (
        <div key={i}>{renderSkeleton()}</div>
      ))}
      <span className="sr-only">Loading content, please wait...</span>
    </div>
  );
}

