// TestimonialCard.tsx
// Provide { quote, author, title, company, photo, companyLogo, linkedIn } from /api/testimonials

interface TestimonialCardProps {
  quote: string;
  author: string;
  title: string;
  company: string;
  photo: string;
  companyLogo?: string;
  linkedIn?: string;
}

export function TestimonialCard({
  quote,
  author,
  title,
  company,
  photo,
  companyLogo,
  linkedIn
}: TestimonialCardProps) {
  return (
    <article className="group relative bg-white rounded-xl p-8 border border-neutral-200 hover:border-primary-500 hover:shadow-xl transition-all duration-250 flex flex-col h-full">
      {/* Quote */}
      <div className="flex-grow mb-6">
        <svg className="w-10 h-10 text-primary-200 mb-4" fill="currentColor" viewBox="0 0 24 24" aria-hidden="true">
          <path d="M14.017 21v-7.391c0-5.704 3.731-9.57 8.983-10.609l.995 2.151c-2.432.917-3.995 3.638-3.995 5.849h4v10h-9.983zm-14.017 0v-7.391c0-5.704 3.748-9.57 9-10.609l.996 2.151c-2.433.917-3.996 3.638-3.996 5.849h3.983v10h-9.983z" />
        </svg>
        <blockquote>
          <p className="font-body text-lg text-neutral-700 leading-relaxed italic">
            "{quote}"
          </p>
        </blockquote>
      </div>

      {/* Author */}
      <footer className="flex items-center gap-4 pt-6 border-t border-neutral-200">
        <img
          src={photo}
          alt={author}
          className="w-14 h-14 rounded-full object-cover"
          loading="lazy"
        />
        <div className="flex-grow">
          <cite className="not-italic">
            {linkedIn ? (
              <a
                href={linkedIn}
                target="_blank"
                rel="noopener noreferrer"
                className="font-heading font-semibold text-neutral-900 hover:text-primary-500 transition-colors duration-200"
                aria-label={`View ${author}'s LinkedIn profile`}
              >
                {author}
              </a>
            ) : (
              <span className="font-heading font-semibold text-neutral-900">{author}</span>
            )}
          </cite>
          <p className="font-body text-sm text-neutral-600">
            {title}, {company}
          </p>
        </div>
        {companyLogo && (
          <img
            src={companyLogo}
            alt={`${company} logo`}
            className="h-8 w-auto opacity-60 group-hover:opacity-100 transition-opacity duration-250"
            loading="lazy"
          />
        )}
      </footer>
    </article>
  );
}

